ALTER TABLE "config_teleoperators" ADD CONSTRAINT "config_teleoperators_configuration_id_robot_configurations_id_fk" FOREIGN KEY ("configuration_id") REFERENCES "public"."robot_configurations"("id") ON DELETE no action ON UPDATE no action;

ALTER TABLE "config_teleoperators" ADD CONSTRAINT "config_teleoperators_teleoperator_id_teleoperator_models_id_fk" FOREIGN KEY ("teleoperator_id") REFERENCES "public"."teleoperator_models"("id") ON DELETE set null ON UPDATE no action;
